mapper.select:
select w.*, b.name b_name, t.name t_name, q.name q_name, t.team_leader t_leader, t.leader_school t_school, t.leader_phone t_leader_phone
from ((work w inner join team t on w.team_id = t.id) inner join question q on w.ques_id = q.id) inner join batch b on q.batch_id = b.id


        and b.name = #{batch_name}


        and t.name like concat('%',#{team_name},'%')


        and w.name like concat('%',#{work_name},'%')


        and q.name like concat('%',#{ques_name},'%')


        and t.team_leader like concat('%',#{team_leader},'%')


        and t.leader_school like concat('%',#{leader_school},'%')


        and t.leader_phone = #{leader_phone}


        and w.reviewed = #{reviewed}

mapper.update:
update city.work
set review_num = #{work_expert}
where id = #{id}

update city.work
set reviewed_num = reviewed_num + 1
where id = #{id}
