mapper.update:
update city.contest
name = #{name}, time_limit = #{time_limit}, question_number = #{question_number}, archive_status = #{archive_status}, contest_id = #{contest_id}, image = #{image}, content = #{content}, start_time = #{start_time}, end_time = #{end_time}, apply_start = #{apply_start}, apply_end = #{apply_end}
where id = #{id}

update city.contest
set image = #{image}
where id = #{id}

mapper.select:
select * from city.contest
and `name` like concat('%',#{name},'%')
and `archive_status` = #{archive_status}
